const users = [
    { email: "hjs@nc.com", password: "123", name: "Kim" },
    { email: "ces@.com", password: "456", name: "Lee" },
    { email: "ssm@test.com", password: "789", name: "Park" },
  ];
  
  export function signIn({ email, password }) {
    const user = users.find(
      (user) => user.email === email && user.password === password
    );
    if (user === undefined) throw new Error();
    return user;
  }
