const users = [
    { email: "ncttssm@ssu.com", password: "0415"},
    { email: "ncttces@ssu.com", password: "0410"},
    { email: "nctthjs@ssu.com", password: "0404"},
    { email: "root@root.com", password: "root"},
  ];
  
  export function signIn({ email, password } : {email: string, password:string}) {
    const user = users.find(
      (user) => user.email === email && user.password === password
    );
    if (user === undefined) throw new Error();
    return user;
  }
