import { render } from "@testing-library/react";
import React, { Component } from "react";

interface DeliveryProps {
    senderName:string;
    senderPn:string;
    recipientName:string;
    recipientPn:string;
    destinationTagId:number;
    destinationTagName:string
}

interface invoiceNumber {
    invoiceNumber:string;
}

class DeliveryConfirm extends Component<DeliveryProps, invoiceNumber> {
    state: invoiceNumber = {
        invoiceNumber: '',
    }

    componentDidMount() {
		this.getnodeList(this.state.currentPage);
	}

	// Get node list from backend
	getnodeList(page: number) {
		var url =
			NODE_URL + '?sink=' + this.props.sink_id + '&page=' + page;

		const data = [];
		this.setState({nodeList: [], pages: 1});

		fetch(url)
			.then((res) => res.json()) 
			.then((data) =>
				page === 1
					? this.setState({ nodeList: data.nodes, pages: data.pages })
					: this.setState({ nodeList: data.nodes})
			)
			.catch((error) => this.setState({pages:0}));//console.error('Error:', error));
	}

    render(){
        return(
            <>
			<div>
				<div style={{ textAlign:"center", marginTop:"100px"}}>
					<label style={{fontSize:"40px"}}>Delivery registration completed!</label>
                    <hr style={{width:"570px", borderColor:"black"}}></hr><br></br>
					<div style={{textAlign:"left", marginLeft:"300px"}}>
                        <label style={{fontSize:"20px"}}>Invoice Number</label>
                        <br></br>
                        <label style={{fontSize:"20px"}}>Destination Tag</label>
                        <label>{this.props.destinationTagName}</label><br></br>
                        <label style={{fontSize:"20px"}}>Sender Name</label>
                        <label>{this.props.senderName}</label><br></br>
                        <label style={{fontSize:"20px"}}>Sender Phone Number</label>
                        <label>{this.props.senderPn}</label><br></br>
                        <label style={{fontSize:"20px"}}>Recipient Name</label>
                        <label>{this.props.recipientName}</label><br></br>
                        <label style={{fontSize:"20px"}}>Recipient Phone Number</label>
                        <label>{this.props.recipientPn}</label>
                    </div>
				</div>
				<div style={{ textAlign:"center", marginTop:"315px"}}>
					<button
						type="button"
						className="btn my-2 my-sm-0" 
						data-toggle="modal"
						style={{ background: '#82CAFA', color : 'white' }}
					>
					Tracking Delivery
					</button>
					</div>
			</div>
			</>
        );
    }
}

export default DeliveryConfirm
