import { render } from "@testing-library/react";
import React, { Component } from "react";
import { DELIVERY_URL } from "./defineUrl";

interface DeliveryProps {
    senderName:string;
    senderPn:string;
    recipientName:string;
    recipientPn:string;
    destinationTagId:number;
    destinationTagName:string
}

interface invoiceNumber {
    invoiceNumber:string;
}

class DeliveryConfirm extends Component<DeliveryProps, invoiceNumber> {
    state: invoiceNumber = {
        invoiceNumber: '',
    }

    componentDidMount() {
		this.getDestinationTag(this.props.destinationTagId);
	}

	getDestinationTag(Id: number) {
		var url =
			DELIVERY_URL + '?id=' + this.props.destinationTagId;

		const data = [];

		fetch(url)
			.then((res) => res.json()) 
			.then((data) => this.setState({ invoiceNumber: data.invoiceNumber})
			)
			.catch((error) => console.error('Error:', error));
	}

    render(){
        return(
            <>
			<div>
				<div style={{ textAlign:"center", marginTop:"100px"}}>
					<label style={{fontSize:"40px"}}>Delivery registration completed!</label>
                    <hr style={{width:"570px", borderColor:"black"}}></hr><br></br>
					<div style={{textAlign:"left", marginLeft:"300px"}}>
                        <label style={{fontSize:"20px"}}>Invoice Number</label>
                        <label style={{fontSize:"20px"}}>{this.state.invoiceNumber}</label><br></br>
                        <label style={{fontSize:"20px"}}>Destination Tag</label>
                        <label style={{fontSize:"20px"}}>{this.props.destinationTagName}</label><br></br>
                        <label style={{fontSize:"20px"}}>Sender Name</label>
                        <label style={{fontSize:"20px"}}>{this.props.senderName}</label><br></br>
                        <label style={{fontSize:"20px"}}>Sender Phone Number</label>
                        <label style={{fontSize:"20px"}}>{loca}</label><br></br>
                        <label style={{fontSize:"20px"}}>Recipient Name</label>
                        <label style={{fontSize:"20px"}}>{this.props.recipientName}</label><br></br>
                        <label style={{fontSize:"20px"}}>Recipient Phone Number</label>
                        <label style={{fontSize:"20px"}}>{this.props.recipientPn}</label>
                    </div>
				</div>
				<div style={{ textAlign:"center", marginTop:"315px"}}>
					<button
						type="button"
						className="btn my-2 my-sm-0" 
						data-toggle="modal"
						style={{ background: '#82CAFA', color : 'white' }}
					>
					Tracking Delivery
					</button>
					</div>
			</div>
			</>
        );
    }
}

export default DeliveryConfirm
